import BASEURL from "./api"

const URL = `${BASEURL}/connections`

const getTotal = () =>
  fetch(URL, {
    headers: { "Access-Control-Allow-Origin": "*" },
    mode: 'no-cors'
  }).then(async resp => {
    if (resp.ok) {
      const data = await resp.json()
      return data
    }
    throw new Error('Não foi possível resgatar o total de connections :(')
  })

export default {
  getTotal
}
